mapper.select:
SELECT * FROM apartment_info
WHERE deal_year = #{year}
AND deal_month = #{mon}
AND location_cd = ${locationCd}

mapper.insert:
INSERT INTO apartment_info
(
deal_amount, build_year, deal_year, deal_month
, deal_day, dong, apartment_name, area_for_exclusive_use
, jibun, flr, location_cd
)
VALUES
(
${deal_amount}, #{build_year}, #{deal_year}, #{deal_month}
, #{deal_day}, #{dong}, #{apartment_name}, #{area_for_exclusive_use}
, #{jibun}, #{flr}, #{location_cd}
)
