mapper.delete:
DELETE FROM t_restaurant
WHERE i_rest = #{i_rest}
AND i_user = #{i_user}

DELETE A FROM t_restaurant_recommend_menu A
INNER JOIN t_restaurant B
ON A.i_rest = B.i_rest
WHERE B.i_rest = #{i_rest}
AND B.i_user = #{i_user}

	AND A.seq = #{seq}

DELETE A FROM t_restaurant_menu A
INNER JOIN t_restaurant B
ON A.i_rest = B.i_rest
WHERE B.i_rest  = #{i_rest}
AND B.i_user = #{i_user}

	AND A.seq = #{seq}

mapper.select:
SELECT i_rest, nm, lat, lng
FROM t_restaurant
WHERE lat BETWEEN #{sw_lat} AND #{ne_lat}
AND lng BETWEEN #{sw_lng} AND #{ne_lng}

mapper.insert:
INSERT INTO t_restaurant_recommend_menu
(seq, i_rest, menu_nm, menu_price, menu_pic)
SELECT IFNULL(MAX(seq), 0) + 1, #{i_rest}, #{menu_nm}, #{menu_price}, #{menu_pic}
FROM t_restaurant_recommend_menu
WHERE i_rest = #{i_rest}

mapper.select:
SELECT seq, menu_nm, menu_price, menu_pic
FROM t_restaurant_recommend_menu

	INNER JOIN t_restaurant B
	ON A.i_rest = B.i_rest
	AND B.i_user = #{i_user}

WHERE i_rest = #{i_rest}

	AND A.seq = #{seq}
